import React,{useState , useEffect , Fragment, FC} from 'react';
import {Button, Container, Divider, Dropdown, Grid, Icon, Input, List, Menu} from 'semantic-ui-react';
import axios from 'axios';

//import './styles.css';


import { IProduct } from '../../models/product';
import SidebarExampleTransitions from './dashboard';
import DashboardLayout from './dashboard';
import { Route } from 'react-router';
import AppDashboard from '../../../feautures/Details/DashboardDetails/AppDashboard';


interface IProps {
  products:IProduct[];
  // selectActivity: (id: string) => void;
  // selectedActivity: IActivity | null;
  // editMode: boolean;
  // setEditMode: (editMode: boolean) => void;
  // setSelectedActivity: (activity: IActivity | null) => void;
  // createActivity: (activity: IActivity) => void;
  // editActivity: (activity: IActivity) => void;
  // deleteActivity: (id: string) => void;
}

const Dashboard = () => { 
  const [products, setActivities] = useState<IProduct[]>([]);

    useEffect(() => {
      axios
        .get<IProduct[]>("http://localhost:5000/api/product")
        .then((response) => {
          let products: IProduct[] = [];
          response.data.forEach((product) => {
            product.productName = product.productName.split(".")[0];
            products.push(product);
          });
          setActivities(products);
        });
    }, []);

    return (
      <div>
        {/* <DashboardLayout/> */}
        <div>
        <Menu stackable>
              <Menu.Item>
                <Button
                  onClick={() =>
                    dispatch({ type: "CHANGE_ANIMATION", animation: "push" })
                  }
                >
                  <Icon name="list" />
                </Button>
              </Menu.Item>

              <Menu.Item>
                <Input className="icon" icon="search" placeholder="Search..." />
              </Menu.Item>

              <Menu.Item position="right">
                <Input
                  action={{ type: "submit", content: "Go" }}
                  placeholder="Navigate to..."
                />
              </Menu.Item>
              <Menu.Item>
                <Button primary negative>
                  Log out
                </Button>
              </Menu.Item>
            </Menu>
        </div>
        <Menu vertical inverted>
        <Menu.Item
          name='account'

        />
        <Menu.Item
          name='settings'

        />
        <Dropdown item text='Display Options'>
          <Dropdown.Menu>
            <Dropdown.Header>Text Size</Dropdown.Header>
            <Dropdown.Item>Small</Dropdown.Item>
            <Dropdown.Item>Medium</Dropdown.Item>
            <Dropdown.Item>Large</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Menu>



              {/* <Route path="/overview/revenue" component={Revenue} exact></Route>
        <Route path="/order" component={Order} exact></Route>
        <Route path="/history" component={History} exact></Route>
        <Route path="/configurations" component={Configurations} exact></Route> */}

      </div>


    );
  }


export default Dashboard;
