import React,{useState , useEffect , Fragment} from 'react';
import {Container, Divider, Grid, List} from 'semantic-ui-react';
import axios from 'axios';
import { IAdresaF } from '../models/adresaF';

import Faturimi from './FaturimiLayout/Faturimi';
//import './styles.css';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Dashboard from './DashboardLayout/dashboard';
import SidebarExampleTransitions from './DashboardLayout/dashboard';
import Sidebar from './DashboardLayout/SideBarDashboard/components/Sidebar';
import AppDashboard from '../../feautures/Details/DashboardDetails/AppDashboard';
import { Overview } from './DashboardLayout/pages/Overview';
import DashboardList from '../../feautures/Details/DashboardDetails/DashboardList';
import { IProduct } from '../models/product';

interface IProps {
  products:IProduct[];
  // selectActivity: (id: string) => void;
  // selectedActivity: IActivity | null;
  // editMode: boolean;
  // setEditMode: (editMode: boolean) => void;
  // setSelectedActivity: (activity: IActivity | null) => void;
  // createActivity: (activity: IActivity) => void;
  // editActivity: (activity: IActivity) => void;
  // deleteActivity: (id: string) => void;
}

const App = () => { 
  const [products, setActivities] = useState<IProduct[]>([]);

    useEffect(() => {
      axios
        .get<IProduct[]>("http://localhost:5000/api/product")
        .then((response) => {
          let products: IProduct[] = [];
          response.data.forEach((product) => {
            product.productName = product.productName.split(".")[0];
            products.push(product);
          });
          setActivities(products);
        });
    }, []);

    return (
      <BrowserRouter>
        <div className="container">
          <h3 className="m-3 d-flex justify-content-center">
            React JS Tutorial
          </h3>
          <Sidebar />
          <Switch>
            {/* <Route path="/" exact />
            <Route path="/dashboard" component={SidebarExampleTransitions} />
            <Route path="/faturimi" component={Faturimi} /> */}
            
            
            
                <Route path="/productmaster"  exact><AppDashboard products={products} /></Route>
                {/* <Route path="/overview/users" component={Users} exact></Route>
                <Route path="/overview/revenue" component={Revenue} exact></Route>
                <Route path="/order" component={Order} exact></Route>
                <Route path="/history" component={History} exact></Route>
                <Route path="/configurations" component={Configurations} exact></Route> */}
            
        
          </Switch>
          
        </div>
      </BrowserRouter>
      
      
    );
  }
 

export default App;
