import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ISector } from "../../../../app/models/sector";
import Kategorite from "../../../nav/Kategorite";
import '../../../../app/layout/HomePageLayout/styles.css';
import { IProduct } from "../../../../app/models/product";
import {
  Wrapper,
  OverlayWrapper,
  Card,
  CardImage,
  CardBody,
  CardTitle,
  CardPrice,
} from "./Styles";
import agent from "../../../../app/API/agent";


export default function HomeItems() {
  const [isHover, setHover] = useState(false);

  const [products, setProducts] = useState<IProduct[]>([]);

  const handleMouseEnter = () => setHover(true);
  const handleMouseLeave = () => setHover(false);
  useEffect(() => {
    agent.Products.productList()
      .then((response) => {
        let products: IProduct[] = [];
        response.forEach((product) => {
          product.productName = product.productName.split(".")[0];
          products.push(product);
        });
      });
  }, []);

  return (
    <div>
       <Wrapper>
      {products.map((product) => (
        <Card>
        <CardImage src="./assets/product1.jpg" />
        <CardBody>
          <CardTitle>{product.productName}</CardTitle>
          <CardPrice>{product.valueOfProduct}</CardPrice>
        </CardBody>
      </Card>
      ))}
    </Wrapper>

    </div>
   
  );
}
