import React, { useState, useEffect, Fragment } from "react";

import { Button, Checkbox, Container, Dropdown, Grid, Header, Icon, Image, Input, Menu, Segment, Sidebar } from "semantic-ui-react";
import axios from "axios";
import './styles.css';
// import { IActivity } from '../models/activity';
// import ActivityDashboard from '../../../features/activities/dashboard/AppDashboard';
import AppDashboard from '../../../feautures/Details/DashboardDetails/AppDashboard';
import { IProduct } from "../../models/product";


  //   const [selectedActivity, setSelectedActivity] = useState<IActivity | null>(
  //     null
  //   );
  //   const [editMode, setEditMode] = useState(false);

  //   const handleOpenCreateForm = () => {
  //     setSelectedActivity(null);
  //     setEditMode(true);
  //   }

  //   const handleCreateActivity = (activity: IActivity) => {
  //     setActivities([...activities, activity]);
  //     setSelectedActivity(activity);
  //     setEditMode(false);
  //   }

  //   const handleEditActivity = (activity: IActivity) => {
  //     setActivities([...activities.filter(a => a.id !== activity.id), activity])
  //     setSelectedActivity(activity);
  //     setEditMode(false);
  //   }

  //   const handleDeleteActivity = (id: string) => {
  //     setActivities([...activities.filter(a => a.id !== id)])
  //   }

  //   const handleSelectActivity = (id: string) => {
  //     setSelectedActivity(activities.filter(a => a.id === id)[0]);
  //     setEditMode(false);
  //   };


  const HorizontalSidebar = ({ animation, direction, visible }) => (
    <Sidebar
      as={Segment}
      animation={animation}
      direction={direction}
      visible={visible}
    >
      <Grid textAlign="center">
        <Grid.Row columns={1}>
          <Grid.Column>
            <Header as="h3">New Content Awaits</Header>
          </Grid.Column>
        </Grid.Row>
        <Grid.Row columns={3}>
          <Grid.Column>
            <Image src="/images/wireframe/media-paragraph.png" />
          </Grid.Column>
          <Grid.Column>
            <Image src="/images/wireframe/media-paragraph.png" />
          </Grid.Column>
          <Grid.Column>
            <Image src="/images/wireframe/media-paragraph.png" />
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </Sidebar>
  );



  function exampleReducer(state, action) {
    switch (action.type) {
      case "CHANGE_ANIMATION":
        return {
          ...state,
          animation: action.animation,
          visible: !state.visible,
        };
      case "CHANGE_DIMMED":
        return { ...state, dimmed: action.dimmed };
      case "CHANGE_DIRECTION":
        return { ...state, direction: action.direction, visible: false };
      default:
        throw new Error();
    }
  }

  function SidebarExampleTransitions() {
    const [products, setActivities] = useState<IProduct[]>([]);

    useEffect(() => {
      axios
        .get<IProduct[]>("http://localhost:5000/api/product")
        .then((response) => {
          let products: IProduct[] = [];
          response.data.forEach((product) => {
            product.productName = product.productName.split(".")[0];
            products.push(product);
          });
          setActivities(products);
        });
    }, []);
    const [state, dispatch] = React.useReducer(exampleReducer, {
      animation: "overlay",
      direction: "left",
      visible: false,
    });
    const options = [
      { key: 1, text: 'Choice 1', value: 1 },
      { key: 2, text: 'Choice 2', value: 2 },
      { key: 3, text: 'Choice 3', value: 3 },
      ]
    const VerticalSidebar = ({ animation, direction, visible }) => (
    <Sidebar
      as={Menu}
      animation={animation}
      direction={direction}
      icon="labeled"
      inverted
      vertical
      visible={visible}
      width="thin"
    >

      <Menu.Item as="a">
        <Icon name="home" />
        <Menu compact>
        <Dropdown text='Dropdown' options={options} simple item />
        </Menu>
      </Menu.Item>
      <Menu.Item as="a">
        <Icon name="gamepad" />
        Games
      </Menu.Item>
      <Menu.Item as="a">
        <Icon name="camera" />
        Channels
      </Menu.Item>
    </Sidebar>
  );

    const { animation, dimmed, direction, visible } = state;
    const vertical = direction === "bottom" || direction === "top";

    return (
      <div>
        <Button.Group>
          <Menu stackable>
            <Menu.Item>
              <Button
                onClick={() =>
                  dispatch({ type: "CHANGE_ANIMATION", animation: "push" })
                }
              >
                <img src="/logo.png" />
              </Button>
            </Menu.Item>

            <Menu.Item>
              <Input className="icon" icon="search" placeholder="Search..." />
            </Menu.Item>

            <Menu.Item position="right">
              <Input
                action={{ type: "submit", content: "Go" }}
                placeholder="Navigate to..."
              />
            </Menu.Item>
            <Menu.Item>
              <Button primary negative>Log out</Button>
            </Menu.Item>
          </Menu>
        </Button.Group>

        <Sidebar.Pushable as={Segment} style={{ overflow: "hidden" }}>
          {vertical && (
            <HorizontalSidebar
              animation={animation}
              direction={direction}
              visible={visible}
            />
          )}
          {!vertical && (
            <VerticalSidebar
              animation={animation}
              direction={direction}
              visible={visible}
            />
          )}

          <Sidebar.Pusher dimmed={dimmed && visible}>
            <Segment basic>
              <AppDashboard products={products} />
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div>
    );

};
export default SidebarExampleTransitions;
