import React, { FC, useEffect, useState } from 'react';
import Sidebar from '../DashboardLayout/SideBarDashboard/components/Sidebar';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { Configurations, History, Order, Overview, Revenue, Users } from './pages/Overview';
import { IProduct } from '../../models/product';
import axios from 'axios';
import AppDashboard from '../../../feautures/Details/DashboardDetails/AppDashboard';
import { Segment, SidebarPushable, SidebarPusher } from 'semantic-ui-react';
interface IProps {
  products: IProduct[];
  // selectActivity: (id: string) => void;
  // deleteActivity: (id: string) => void;
}

const Dashboard: FC = () => {
  const [products, setActivities] = useState<IProduct[]>([]);

  function exampleReducer(state, action) {
    switch (action.type) {
      case "CHANGE_ANIMATION":
        return {
          ...state,
          animation: action.animation,
          visible: !state.visible,
        };

      case "CHANGE_DIRECTION":
        return { ...state, direction: action.direction, visible: false };
      default:
        throw new Error();
    }
  }
  const [state, dispatch] = React.useReducer(exampleReducer, {
    animation: "overlay",
    direction: "left",
    visible: false,
  });


  const { animation, dimmed, direction, visible } = state;
  const vertical = direction === "bottom" || direction === "top";

  useEffect(() => {
    axios
      .get<IProduct[]>("http://localhost:5000/api/product")
      .then((response) => {
        let products: IProduct[] = [];
        response.data.forEach((product) => {
          product.productName = product.productName.split(".")[0];
          products.push(product);
        });
        setActivities(products);
      });
  }, []);
    return (
      <Router>
        <Sidebar />
        <Switch>
          <SidebarPushable as={Segment} style={{ overflow: "hidden" }}>
            <SidebarPusher dimmed={dimmed && visible}>
              <Segment basic>
                <AppDashboard products={products} />
              </Segment>
            </SidebarPusher>
          </SidebarPushable>
          <Route path="/dashboard/productmaster">
            <AppDashboard products={products} />
          </Route>
          <Route path="/overview/users" component={Users} exact></Route>
          <Route path="/overview/revenue" component={Revenue} exact></Route>
          <Route path="/order" component={Order} exact></Route>
          <Route path="/history" component={History} exact></Route>
          <Route
            path="/configurations"
            component={Configurations}
            exact
          ></Route>
        </Switch>
      </Router>
    );
};

export default Dashboard;
