import React from "react";
import { Link } from "react-router-dom";
import { Button, Header, Icon, Input, Item, Label, Menu } from "semantic-ui-react";
import { ISector } from "../../../../app/models/sector";
import Kategorite from "../../../nav/Kategorite";
import '../../../../app/layout/HomePageLayout/styles.css';
import { IProduct } from "../../../../app/models/product";

interface IProps {
  products:IProduct[];
  // selectActivity: (id: string) => void;
  // selectedActivity: IActivity | null;
  // editMode: boolean;
  // setEditMode: (editMode: boolean) => void;
  // setSelectedActivity: (activity: IActivity | null) => void;
  // createActivity: (activity: IActivity) => void;
  // editActivity: (activity: IActivity) => void;
  // deleteActivity: (id: string) => void;
}

  const HomeItems: React.FC<IProps> = ({
    products,
    // selectProduct,
    // selectedProduct,
    // editMode,
    // setEditMode,
    // setSelectedProduct,
    // createProduct,
    // editProduct,
    // deleteProduct,
    // openCreateForm
  }) => {
    return (
      <Item.Group className="itemsHome">
        {products.map((product) => (
          <Button animated>
            <Button.Content visible>
              <Item key={product.productId} className="Items">
                <Item.Content className={product.productId}>
                  <Item.Header as="a">
                    <img className="itemPhoto" src="/assets/product1.jpg" />
                  </Item.Header>
                  <Item.Meta>
                    <span className="title">{product.productName}</span>
                  </Item.Meta>
                  <Item.Meta>
                    <span className="value">{product.valueOfProduct}</span>
                  </Item.Meta>
                  <Item.Meta>
                    <Button animated>
                      <Button.Content visible>SHIKO</Button.Content>
                      <Button.Content hidden>
                        <Icon name="arrow right" />
                      </Button.Content>
                    </Button>
                    <Button animated="vertical">
                      <Button.Content hidden>Shop</Button.Content>
                      <Button.Content visible>
                        <Icon name="shop" />
                      </Button.Content>
                    </Button>
                    <Button animated="fade">
                      <Button.Content visible>
                        Sign-up for a Pro account
                      </Button.Content>
                      <Button.Content hidden>$12.99 a month</Button.Content>
                    </Button>
                  </Item.Meta>
                </Item.Content>
              </Item>
            </Button.Content>
          </Button>
        ))}
      </Item.Group>
    );
  };
  
  export default HomeItems;
