import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Navbar from '../../../feautures/nav/NavBar';
import { ISector } from '../../models/sector';

export const Home = () => {
    const [sectors, setSectors] = useState<ISector[]>([]);


    useEffect(() => {
        axios
          .get<ISector[]>("http://localhost:5000/api/sector")
          .then((response) => {
            let sectors: ISector[] = [];
            response.data.forEach((sector) => {
              sector.sectorName = sector.sectorName.split(".")[0];
              sectors.push(sector);
            });
            setSectors(sectors);
          });
      }, []);

      const mainSlider = 
      <div className="slider">
        <div className="slider-content active">
          <img src="https://image.shutterstock.com/image-photo/garhwal-himalaya-mountain-range-scenic-260nw-1491736055.jpg" alt=""/>
        </div>
        <div className="slider-content not-active">
          <img src="https://image.shutterstock.com/image-photo/mountains-under-mist-morning-amazing-260nw-1725825019.jpg" alt=""/>
        </div>
        <div className="slider-content not-active">
          <img src="https://image.shutterstock.com/image-photo/panoramic-landscape-great-himalayas-mountain-260nw-1935022454.jpg" alt=""/>
        </div>
      </div>;
      
      var divElements = document.getElementsByClassName('slider-content');
      var sliderIndex = 0;

      document.getElementsByClassName('slider')[0].addEventListener('click',function(event){
        divElements[sliderIndex].classList.remove('active')
        divElements[sliderIndex].classList.add('not-active')

        sliderIndex++;
        if(sliderIndex==divElements.length) sliderIndex=0;
          divElements[sliderIndex].classList.remove('active')
          divElements[sliderIndex].classList.add('not-active')

      
        



      })
      
    return (
        <div>
            <Navbar sectors={sectors}  />
            {mainSlider}
        </div>
    )
}
