import React,{useState , useEffect} from 'react';
import {Header ,Icon, List} from 'semantic-ui-react';
import axios from 'axios';
import { IAdresaF } from '../models/adresaF';
import { NavBar } from '../../feautures/nav/NavBar';


const App = () => { //per me i tregu komponentit tone per interfacin IState , {} e shprazet eshte per properties , e dyta per state

  const [AdresaF, setAdresaF] = useState<IAdresaF[]>([]);

  useEffect(() => {
       axios.get<IAdresaF[]>('https://localhost:5001/api/AdresaFaturimit').then((response) => { //ktu ja kemi tregu axios se qfare lloji te adreses po kthejm , pra jo any me kon pasi po mundohemi me bo strong typing
      setAdresaF(response.data)
    });
  }, []); //kto dy kllapa sigurojne qe useEffect me run vec nje here , per shkak se qdoher kur komponenti renderet kjo use effect metoda kallet vec tu shtu [] e ndalim kete sjellje

    return (
      <div>
       <NavBar/>
       <List>
         {AdresaF.map((adresa) => (
           <List.Item key={adresa.id}>{adresa.emri} {adresa.mbiemri}</List.Item>
         ))}
       </List>
      </div>
    );
  }


export default App;
