import React, { useState, useEffect, Fragment } from "react";

import { Button, Checkbox, Container, Dropdown, Grid, Header, Icon, Image, Input, Menu, Segment, Sidebar } from "semantic-ui-react";
import axios from "axios";
import './styles.css';
// import { IActivity } from '../models/activity';
// import ActivityDashboard from '../../../features/activities/dashboard/AppDashboard';
import AppDashboard from '../../../feautures/Details/DashboardDetails/AppDashboard';
import { IProduct } from "../../models/product";
import { SidebarData } from "./SideBarDashboard/components/SidebarData";
import Submenu from "./SideBarDashboard/components/Submenu";
import { Overview } from "./pages/Overview";
import { BrowserRouter, Link, Route, Switch } from "react-router-dom";
import styled from "styled-components";
import { AiOutlineClose, AiOutlineMenu } from "react-icons/ai";
import { IconContext } from "react-icons";


  //   const [selectedActivity, setSelectedActivity] = useState<IActivity | null>(
  //     null
  //   );
  //   const [editMode, setEditMode] = useState(false);

  //   const handleOpenCreateForm = () => {
  //     setSelectedActivity(null);
  //     setEditMode(true);
  //   }

  //   const handleCreateActivity = (activity: IActivity) => {
  //     setActivities([...activities, activity]);
  //     setSelectedActivity(activity);
  //     setEditMode(false);
  //   }

  //   const handleEditActivity = (activity: IActivity) => {
  //     setActivities([...activities.filter(a => a.id !== activity.id), activity])
  //     setSelectedActivity(activity);
  //     setEditMode(false);
  //   }

  //   const handleDeleteActivity = (id: string) => {
  //     setActivities([...activities.filter(a => a.id !== id)])
  //   }

  //   const handleSelectActivity = (id: string) => {
  //     setSelectedActivity(activities.filter(a => a.id === id)[0]);
  //     setEditMode(false);
  //   };
  const Nav = styled.div`
    display: flex;
    justify-content: flex-start;
    align-items: center;
    height: 5rem;
    background-color: black;
`;

const SidebarNav = styled.div<{ sidebar: boolean }>`
    width: 250px;
    height: 100vh;
    background-color: black;
    position: fixed;
    top: 0;
    left: ${({ sidebar }) => (sidebar ? '0' : '-100%')};
    transition: 350ms;
`;

const NavIcon = styled(Link)`
    display: flex;
    justify-content: flex-start;
    align-items: center;
    height: 5rem;
    font-size: 2rem;
    margin-left: 2rem;
`;

const SidebarWrap = styled.div``;





  function exampleReducer(state, action) {
    switch (action.type) {
      case "CHANGE_ANIMATION":
        return {
          ...state,
          animation: action.animation,
          visible: !state.visible,
        };
      case "CHANGE_DIMMED":
        return { ...state, dimmed: action.dimmed };
      case "CHANGE_DIRECTION":
        return { ...state, direction: action.direction, visible: false };
      default:
        throw new Error();
    }
  }

  function SidebarExampleTransitions() {
    const [products, setActivities] = useState<IProduct[]>([]);

    useEffect(() => {
      axios
        .get<IProduct[]>("http://localhost:5000/api/product")
        .then((response) => {
          let products: IProduct[] = [];
          response.data.forEach((product) => {
            product.productName = product.productName.split(".")[0];
            products.push(product);
          });
          setActivities(products);
        });
    }, []);
    const [state, dispatch] = React.useReducer(exampleReducer, {
      animation: "overlay",
      direction: "left",
      visible: false,
    });
    const [sidebar, setSidebar] = useState(false);
    const showSidebar = () => setSidebar(!sidebar);
    const VerticalSidebar = ({ animation, direction, visible }) => (


        <Menu.Item as="a">
          <IconContext.Provider value={{ color: "#fff" }}>
            <Nav>
              <NavIcon to="#" onClick={showSidebar}>
                <AiOutlineMenu />
              </NavIcon>
            </Nav>
            <SidebarNav sidebar={sidebar}>
              <SidebarWrap>
              <Menu.Item>
                <Button
                  onClick={() =>
                    dispatch({ type: "CHANGE_ANIMATION", animation: "push" })
                  }
                >
                  <Icon name="list" />
                </Button>
              </Menu.Item>
                {SidebarData.map((item, index) => {
                  return <Submenu item={item} key={index} />;
                })}
              </SidebarWrap>
            </SidebarNav>
          </IconContext.Provider>
        </Menu.Item>

    );

    const { animation, dimmed, direction, visible } = state;
    const vertical = direction === "bottom" || direction === "top";

    return (
      <div>
        <div className="test3">

            <Menu stackable>
              <Menu.Item>
                <Button
                  onClick={() =>
                    dispatch({ type: "CHANGE_ANIMATION", animation: "push" })
                  }
                >
                  <Icon name="list" />
                </Button>
              </Menu.Item>

              <Menu.Item>
                <Input className="icon" icon="search" placeholder="Search..." />
              </Menu.Item>

              <Menu.Item position="right">
                <Input
                  action={{ type: "submit", content: "Go" }}
                  placeholder="Navigate to..."
                />
              </Menu.Item>
              <Menu.Item>
                <Button primary negative>
                  Log out
                </Button>
              </Menu.Item>
            </Menu>

        </div>

        <Sidebar.Pushable as={Segment} style={{ overflow: "hidden" }}>

          {!vertical && (
            <VerticalSidebar
              animation={animation}
              direction={direction}
              visible={visible}
            />
          )}

          <Sidebar.Pusher dimmed={dimmed && visible}>
            <Segment basic>
              <div>
              <BrowserRouter>
                <Switch>
                <Route path="/configurations" component={Overview} exact></Route>                
                </Switch>
              </BrowserRouter>
              </div>

            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div>
    );

};
export default SidebarExampleTransitions;
