import React from "react";
import { Link } from "react-router-dom";
import { ISector } from "../../../../app/models/sector";
import Kategorite from "../../../nav/Kategorite";
import '../../../../app/layout/HomePageLayout/styles.css';
import { IProduct } from "../../../../app/models/product";
import {
  Wrapper,
  OverlayWrapper,
  Card,
  CardImage,
  CardBody,
  CardTitle,
  CardPrice,
  Icon,
} from "./Styles";

interface IProps {
  products:IProduct[];
  // selectActivity: (id: string) => void;
  // selectedActivity: IActivity | null;
  // editMode: boolean;
  // setEditMode: (editMode: boolean) => void;
  // setSelectedActivity: (activity: IActivity | null) => void;
  // createActivity: (activity: IActivity) => void;
  // editActivity: (activity: IActivity) => void;
  // deleteActivity: (id: string) => void;
}

export default function HomeItems({ addProductToCart, ...props }) {
  const [isHover, setHover] = useState(false);

  const handleMouseEnter = () => setHover(true);
  const handleMouseLeave = () => setHover(false);

  return (
    <Wrapper
      onMouseEnter={() => handleMouseEnter()}
      onMouseLeave={() => handleMouseLeave()}
    >
      {isHover && (
        <OverlayWrapper onClick={() => addProductToCart({ ...props })}>
          <Icon icon={faCartPlus} />
        </OverlayWrapper>
      )}

      <Card>
        <CardImage src={props.imageURL} />
        <CardBody>
          <CardTitle>{props.title}</CardTitle>
          <CardPrice>${props.price}</CardPrice>
        </CardBody>
      </Card>
    </Wrapper>
  );
}
