import React,{useState , useEffect } from 'react';
import {Button,  Input, Menu} from 'semantic-ui-react';
import axios from 'axios';
import { IProduct } from '../../../models/product';
import { Link } from 'react-router-dom';
import { useHistory } from "react-router-dom";
import '../styles.css';



interface IProps {
  products:IProduct[];
  // selectActivity: (id: string) => void;
  // selectedActivity: IActivity | null;
  // editMode: boolean;
  // setEditMode: (editMode: boolean) => void;
  // setSelectedActivity: (activity: IActivity | null) => void;
  // createActivity: (activity: IActivity) => void;
  // editActivity: (activity: IActivity) => void;
  // deleteActivity: (id: string) => void;
}

const Dash = () => { 
  const [products, setActivities] = useState<IProduct[]>([]);

    useEffect(() => {
      axios
        .get<IProduct[]>("http://localhost:5000/api/product")
        .then((response) => {
          let products: IProduct[] = [];
          response.data.forEach((product) => {
            product.productName = product.productName.split(".")[0];
            products.push(product);
          });
          setActivities(products);
        });
    }, []);

    let history = useHistory();
    // const [showMenu,setShowMenu]=useState(false);
    function toggleMenu() {
      
      var menuBox = document.getElementById('menu-box');    
      if(menuBox!=null){
        if (menuBox.style.display == "iverted") {
          // if is menuBox displayed, hide it
          menuBox.style.display = "none";
        } else {
          // if is menuBox hidden, display it
          menuBox.style.display = "block";
        }
      }
    }

    return (
      <div>
        {/* <DashboardLayout/> */}
        <div>
          <Menu stackable>
            <a href="/">
              <img className="icona" src="/assets/logo.png" alt="Logo"></img>
            </a>
            <Menu.Item>
              <Input className="icon" icon="search" placeholder="Search..." />
            </Menu.Item>
            <Menu.Item position="right">
              <Input
                action={{ type: "submit", content: "Go" }}
                placeholder="Navigate to..."
              />
            </Menu.Item>
            <Menu.Item>
              <Button primary negative>
                Log out
              </Button>
            </Menu.Item>
          </Menu>
        </div>

        <Menu vertical inverted>
          <Menu.Item>
            <h3>Product Master</h3>
          </Menu.Item>
          <Menu.Item
            className="itemAdmins"
            name="Product"
            as={Link}
            to="/dashboard/productmaster/product"
          />
          <Menu.Item
            className="itemAdmins"
            name="Sectors"
            as={Link}
            to="/dashboard/productmaster/sectors"
          />
          <Menu.Item
            className="itemAdmins"
            name="Brands"
            as={Link}
            to="/dashboard/productmaster/brands"
          />
          <Menu.Item>
            <h3>Users</h3>
          </Menu.Item>
          {/* <Icon name="list" /> */}
          <Menu.Item className="itemAdmins" name="Admins" />
          <Menu.Item className="itemAdmins" name="Clients" />

          {/* <Dropdown item text="Display Options">
                <Dropdown.Menu>
                  <Dropdown.Header>Text Size</Dropdown.Header>
                  <Dropdown.Item>Small</Dropdown.Item>
                  <Dropdown.Item>Medium</Dropdown.Item>
                  <Dropdown.Item>Large</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown> */}
        </Menu>

        {/* <Route path="/overview/revenue" component={Revenue} exact></Route>
        <Route path="/order" component={Order} exact></Route>
        <Route path="/history" component={History} exact></Route>
        <Route path="/configurations" component={Configurations} exact></Route> */}
      </div>
    );
  }
 

export default Dash;
